import React, { useState, useEffect } from 'react';
import './Login.css';

const Login = () => {
  const [isSignup, setIsSignup] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 768);

  const [formData, setFormData] = useState({
    name: '',
    email: '',
    mobile: '',
    password: '',
    signinEmail: '',
    signinPassword: '',
  });

  const [errors, setErrors] = useState({});

  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth <= 768);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const toggleForm = () => {
    setIsSignup(!isSignup);
    setErrors({});
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const validateSignup = () => {
    const newErrors = {};
    if (!/^[A-Za-z]{2,}(?: [A-Za-z]{2,})+$/.test(formData.name)) {
      newErrors.name = 'Enter first and last name (min 2 letters each)';
    }
    if (!/^[\w-.]+@([\w-]+\.)+[\w-]{2,4}$/.test(formData.email)) {
      newErrors.email = 'Enter a valid email';
    }
    if (!/^\d{10}$/.test(formData.mobile)) {
      newErrors.mobile = 'Enter a 10-digit mobile number';
    }
    if (!/^(?=.*[A-Za-z])(?=.*\d).{8,}$/.test(formData.password)) {
      newErrors.password = 'Password must be 8+ chars with letters and numbers';
    }
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const validateSignin = () => {
    const newErrors = {};
    if (!/^[\w-.]+@([\w-]+\.)+[\w-]{2,4}$/.test(formData.signinEmail)) {
      newErrors.signinEmail = 'Enter a valid email';
    }
    if (formData.signinPassword.length < 8) {
      newErrors.signinPassword = 'Password must be at least 8 characters';
    }
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSignup = () => {
    if (validateSignup()) {
      alert('Signup successful!');
    }
  };

  const handleSignin = () => {
    if (validateSignin()) {
      alert('Signin successful!');
    }
  };

  return (
    <div className="container login-body mt-5">
      {isMobile && (
        <div className="mobile-toggle-buttons">
          <button className="stl-btns" onClick={() => setIsSignup(false)}>Sign In</button>
          <button className="stl-btns" onClick={() => setIsSignup(true)}>Sign Up</button>
        </div>
      )}

      <div className={`login-container ${isSignup ? 'show-signup' : ''}`}>
        {/* Sign In Form */}
        {!isMobile || !isSignup ? (
          <div className="login-form sign-in-form">
            <h2 className='h2-stl mt-5'>Welcome</h2>
            <label className='label-stl'>
              <span className='span-stl'>Email</span>
              <input type="email" name="signinEmail" className='input-taking' value={formData.signinEmail} onChange={handleInputChange} />
              {errors.signinEmail && <p className="error-text">{errors.signinEmail}</p>}
            </label>
            <label className='label-stl'>
              <span className='span-stl'>Password</span>
              <input type="password" name="signinPassword" className='input-taking' value={formData.signinPassword} onChange={handleInputChange} />
              {errors.signinPassword && <p className="error-text">{errors.signinPassword}</p>}
            </label>
            <p className="forgot-password">Forgot password?</p>
            <button type="button" className="submit-btns stl-btns" onClick={handleSignin}>Sign In</button>
          </div>
        ) : null}

        {/* Toggle and Signup Form */}
        {!isMobile && (
          <div className="form-toggle-section">
            <div className="form-toggle-image">
              <div className="form-toggle-text show-signup-text">
                <h3 className='h3-stl'>Don't have an account? Please Sign up!</h3>
              </div>
              <div className="form-toggle-text show-signin-text">
                <h3 className='h3-stl'>If you already have an account, just sign in.</h3>
              </div>
              <div className="form-toggle-btn" onClick={toggleForm}>
                <span className="signup-label span-stl">Sign Up</span>
                <span className="signin-label span-stl">Sign In</span>
              </div>
            </div>
          </div>
        )}

        {(isMobile && isSignup) || !isMobile ? (
          <div className="signup-form">
            <h2 className='h2-stl mt-lg-5'>Create your Account</h2>
            <label className='label-stl'>
              <span className='span-stl'>Full Name</span>
              <input type="text" name="name" className='input-taking' value={formData.name} onChange={handleInputChange} />
              {errors.name && <p className="error-text">{errors.name}</p>}
            </label>
            <label className='label-stl'>
              <span className='span-stl'>Email</span>
              <input type="email" name="email" className='input-taking' value={formData.email} onChange={handleInputChange} />
              {errors.email && <p className="error-text">{errors.email}</p>}
            </label>
            <label className='label-stl'>
              <span className='span-stl'>Mobile No</span>
              <input type="text" name="mobile" className='input-taking' value={formData.mobile} onChange={handleInputChange} />
              {errors.mobile && <p className="error-text">{errors.mobile}</p>}
            </label>
            <label className='label-stl'>
              <span className='span-stl'>Password</span>
              <input type="password" name="password" className='input-taking' value={formData.password} onChange={handleInputChange} />
              {errors.password && <p className="error-text">{errors.password}</p>}
            </label>
            <button type="button" className="submit-btns stl-btns" onClick={handleSignup}>Sign Up</button>
          </div>
        ) : null}
      </div>
    </div>
  );
};

export default Login;
