import React, { useEffect, useState } from "react";
import axios from "axios";

const categories = ["house", "room", "sofa", "bathroom"];

const Services = () => {
  const [selectedCategory, setSelectedCategory] = useState("home");
  const [services, setServices] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const fetchServices = async (category) => {
    setLoading(true);
    setError("");
    try {
      const response = await axios.get(
        `http://localhost:7001/api/auth/services/${category}`
      );
      setServices(response.data?.services || []);
    } catch (err) {
      setError("Failed to fetch services");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchServices(selectedCategory);
  }, [selectedCategory]);

  return (
    <div className="p-6 max-w-6xl mx-auto">
      <h1 className="text-3xl font-bold mb-4 text-center">Our Services</h1>

      <div className="mb-6 flex justify-center">
        <select
          className="p-2 border border-gray-300 rounded-md text-lg"
          value={selectedCategory}
          onChange={(e) => setSelectedCategory(e.target.value)}
        >
          {categories.map((cat) => (
            <option key={cat} value={cat}>
              {cat.charAt(0).toUpperCase() + cat.slice(1)} Service
            </option>
          ))}
        </select>
      </div>

      {loading ? (
        <p className="text-center">Loading...</p>
      ) : error ? (
        <p className="text-center text-red-500">{error}</p>
      ) : services.length === 0 ? (
        <p className="text-center">No services found for {selectedCategory}</p>
      ) : (
        <div className="grid gap-6 grid-cols-1 sm:grid-cols-2 lg:grid-cols-3">
          {services.map((service, idx) => (
            <div
              key={idx}
              className="bg-white shadow-md rounded-lg overflow-hidden hover:shadow-xl transition-shadow duration-300"
            >
              <div className="p-4">
                <h2 className="text-xl font-semibold mb-2">
                  {service.service || "Untitled Service"}
                </h2>
                <p className="text-gray-600 text-sm">
                  {service.description || "No description provided."}
                </p>
                <p className="text-gray-800 font-medium mt-2">
                  Price: â‚¹{service.price}
                </p>
              </div>
              {/* Optional Placeholder Image */}
              <img
                src="https://via.placeholder.com/400x200?text=Service+Image"
                alt={service.service}
                className="w-full h-48 object-cover"
              />
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Services;
