import React, { useState } from 'react';
import './Login.css';

const Login = () => {
    const [isSignUp, setIsSignUp] = useState(false);

    const toggleAuthMode = () => {
        setIsSignUp(prev => !prev);
    };

    return (
        <div className={`auth-container ${isSignUp ? 'signup-active' : ''}`}>
            <div className="auth-sub-container">
                <div className="auth-image-wrapper">
                    <div className="auth-image-overlay"></div>
                    <div className="auth-text-container">
                        <h3>{isSignUp ? "If you already have an account, just sign in." : "Don't have an account? Please Sign up!"}</h3>
                    </div>
                    <div className="auth-toggle-btn" onClick={toggleAuthMode}>
                        <span>{isSignUp ? "Sign In" : "Sign Up"}</span>
                    </div>
                </div>

                {/* Sign In Form */}
                {!isSignUp && (
                    <div className="auth-form-wrapper auth-sign-in">
                        <h2>Welcome</h2>
                        <label className="auth-label">
                            <span>Email</span>
                            <input type="email" className="auth-input" />
                        </label>
                        <label className="auth-label">
                            <span>Password</span>
                            <input type="password" className="auth-input" />
                        </label>
                        <p className="auth-forgot-pass">Forgot password?</p>
                        <button type="button" className="auth-submit-btn">Sign In</button>
                    </div>
                )}

                {/* Sign Up Form */}
                {isSignUp && (
                    <div className="auth-form-wrapper auth-sign-up">
                        <h2>Create your Account</h2>
                        <label className="auth-label">
                            <span>Name</span>
                            <input type="text" className="auth-input" />
                        </label>
                        <label className="auth-label">
                            <span>Email</span>
                            <input type="email" className="auth-input" />
                        </label>
                        <label className="auth-label">
                            <span>Password</span>
                            <input type="password" className="auth-input" />
                        </label>
                        <button type="button" className="auth-submit-btn">Sign Up</button>
                    </div>
                )}
            </div>
        </div>
    );
};

export default Login;
